AOS.init(),
  window.addEventListener('load', () => {
    /* Страница загружена, включая все ресурсы */
    const preloader =
      document.querySelector('.preloader'); /* находим блок Preloader */
    preloader.classList.add(
      'preloader_hidden'
    ); /* добавляем ему класс для скрытия */
  });
const swiper = new Swiper('.swiper', {
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
    scrollbar: {},
  }),
  anchors = document.querySelectorAll('a[href^="#"]');
for (let e of anchors)
  e.addEventListener('click', function (t) {
    t.preventDefault();
    const n = e.hasAttribute('href') ? e.getAttribute('href') : 'body';
    document
      .querySelector(n)
      .scrollIntoView({ behavior: 'smooth', block: 'start' });
  });
const menuBtn = document.querySelector('.header__burger'),
  menuNav = document.querySelector('.header__nav'),
  menuUserNav = document.querySelector('.header__user-nav');
menuBtn.addEventListener('click', () => {
  menuBtn.classList.toggle('active'),
    menuNav.classList.toggle('active'),
    menuUserNav.classList.toggle('active');
}),
  menuBtn.addEventListener('click', () => {
    menuBtn.classList.remove('active');
  }),
  menuUserNav.addEventListener('click', () => {
    menuUserNav.classList.remove('active');
  }),
  menuNav.addEventListener('click', () => {
    menuNav.classList.remove('active');
  });
